package strings;

/**
 * @author Zhongyang MA
 * @date Mar/24/2021
 * @description 最长重复子串
 * 滑动窗口法
 */
public class LongestRepeatSubstring {

    public static int solve(String a) {
        int winLen = a.length()/2;
        while(winLen > 0) {
            int start = 0;
            while(start <= a.length() - winLen*2) {
                if(a.substring(start, start+winLen).equals(a.substring(start+winLen, start+winLen*2))) {
                    return winLen*2;
                }
                start++;
            }
            winLen--;
        }
        return 0;
    }

    public static void main(String[] args) {
        //
        String s1 = "abcabca";
        System.out.println(solve(s1));

        //
        String s2 = "";
        System.out.println(solve(s2));
    }

}
